import React from 'react';
import './App.css';
import 'react-awesome-button/dist/themes/theme-c137.css';
import Pad from './childComponents/Pad';
import VolumeSlider from './childComponents/volumeSlider';
import OnOff from './childComponents/onOff';



class App extends React.Component{
  constructor(props){
    super(props);
    this.state = {
      playList: 'BANK 1',
      onOff: 'ON',
      keyPressed: 'Q',
      volume: 50
    };
    this.aud = [];
  }

  componentWillMount(){
    document.addEventListener("keydown",this.makeSound.bind(this));
  }

  volumeHandler = (event) => {
    this.setState({volume:event.target.value});
  }

  makeSound = (e) => {  
    if(this.state.onOff === 'OFF' || (e.type === 'keydown' && e.key.toUpperCase() !== this.state.keyPressed)) return;
    var aud = e.type === 'keydown'?  this.aud.filter(item => e.key.toUpperCase()===item.id)[0]: e.currentTarget.firstChild;
    aud.currentTime = 0;
    aud.volume = this.state.volume/100;
    aud.play();
  }
  
  changeOnOff = () => {
    this.setState((state) => ({
      onOff: state.onOff === 'ON'? 'OFF':'ON'
    }));
  }

  changePlayList = () => {
    this.setState((state) => ({
      playList: state.playList === 'BANK 1'? 'BANK 2':'BANK 1'
    }));
  }    

  render() {
    return(
      <div className="App">      
        <div className="drumMachine">
          <div className="Controls">
          <VolumeSlider parVolume={this.state.volume} changeVol={this.volumeHandler}></VolumeSlider>
            <OnOff parOnOff={this.state.onOff} parChangeOnOff={this.changeOnOff}>{this.state.onOff}</OnOff>
            <div onClick={this.changePlayList}>
              <button>{this.state.playList}</button>
            </div>
            <button>
              {this.state.playList === 'BANK 1'? playlistOne.filter(x => x.key === this.state.keyPressed)[0].id : playlistTwo.filter(x => x.key === this.state.keyPressed)[0].id}
            </button>
          </div>
          <div className="Grid" id="drum-machine">
            <Pad paramLetter='Q' paramOnOff={this.state.onOff} parVolume={this.state.volume} paramSound={this.state.playList === 'BANK 1'? playlistOne[0].url: playlistTwo[0].url} sendKey={this.makeSound} audioRef={(audioFromChild)=>this.aud[0]=audioFromChild}></Pad>
            <Pad paramLetter='W' paramOnOff={this.state.onOff} parVolume={this.state.volume} paramSound={this.state.playList === 'BANK 1'? playlistOne[1].url: playlistTwo[1].url} sendKey={this.makeSound} audioRef={(audioFromChild)=>this.aud[1]=audioFromChild}></Pad>
            <Pad paramLetter='E' paramOnOff={this.state.onOff} parVolume={this.state.volume} paramSound={this.state.playList === 'BANK 1'? playlistOne[2].url: playlistTwo[2].url} sendKey={this.makeSound} audioRef={(audioFromChild)=>this.aud[2]=audioFromChild}></Pad>
            <Pad paramLetter='A' paramOnOff={this.state.onOff} parVolume={this.state.volume} paramSound={this.state.playList === 'BANK 1'? playlistOne[3].url: playlistTwo[3].url} sendKey={this.makeSound} audioRef={(audioFromChild)=>this.aud[3]=audioFromChild}></Pad>
            <Pad paramLetter='S' paramOnOff={this.state.onOff} parVolume={this.state.volume} paramSound={this.state.playList === 'BANK 1'? playlistOne[4].url: playlistTwo[4].url} sendKey={this.makeSound} audioRef={(audioFromChild)=>this.aud[4]=audioFromChild}></Pad>
            <Pad paramLetter='D' paramOnOff={this.state.onOff} parVolume={this.state.volume} paramSound={this.state.playList === 'BANK 1'? playlistOne[5].url: playlistTwo[5].url} sendKey={this.makeSound} audioRef={(audioFromChild)=>this.aud[5]=audioFromChild}></Pad>
            <Pad paramLetter='Z' paramOnOff={this.state.onOff} parVolume={this.state.volume} paramSound={this.state.playList === 'BANK 1'? playlistOne[6].url: playlistTwo[6].url} sendKey={this.makeSound} audioRef={(audioFromChild)=>this.aud[6]=audioFromChild}></Pad>
            <Pad paramLetter='X' paramOnOff={this.state.onOff} parVolume={this.state.volume} paramSound={this.state.playList === 'BANK 1'? playlistOne[7].url: playlistTwo[7].url} sendKey={this.makeSound} audioRef={(audioFromChild)=>this.aud[7]=audioFromChild}></Pad>
            <Pad paramLetter='C' paramOnOff={this.state.onOff} parVolume={this.state.volume} paramSound={this.state.playList === 'BANK 1'? playlistOne[8].url: playlistTwo[8].url} sendKey={this.makeSound} audioRef={(audioFromChild)=>this.aud[8]=audioFromChild}></Pad>
          </div>         
        </div>
      </div>
    );
  }
}



const playlistOne = [
  {
    key: 'Q',
    id: 'Heater-1',
    url: 'https://s3.amazonaws.com/freecodecamp/drums/Heater-1.mp3'
  }, {
    key: 'W',
    id: 'Heater-2',
    url: 'https://s3.amazonaws.com/freecodecamp/drums/Heater-2.mp3'
  }, {
    key: 'E',
    id: 'Heater-3',
    url: 'https://s3.amazonaws.com/freecodecamp/drums/Heater-3.mp3'
  }, {
    key: 'A',
