import {turnActionType} from '../actions/turnOnOff';

function onoff(state = true, action) {
    switch (action.type) {
        case turnActionType: {
            return Object.assign({}, state, {payload: action.payload});
        }
        default:
            return state;
    }
}

export default onoff;
