import React from 'react';


const Pad = (props)=>{
  return(
    <button  onClick={props.sendKey} className={props.keyActive === props.parLetter? 'drum-pad activated': 'drum-pad'} value={props.parLetter}> {props.parLetter}
      <audio src={props.parSound} className="aud"  id={props.parLetter}></audio> 
    </button>
  );
}


export default Pad;
