import React from 'react';
import store from '../../redux/store';
import connect from 'react-redux';

const VolumeSlider = (props) => {
  const { onoff, propMuted, muteVolume, vol, changeVol } = props;
  return (
    <div className="divVolumeSlider">
      <div className='inputSliderWrapper'>
        <input className={onoff ? 'sliderOn' : 'sliderOff'} type="range" min='0' max='100' value={vol} onChange={changeVol}></input>
      </div>
      <div className='iconWrapper'>
        <i onClick={muteVolume} className={propMuted && onoff ? 'iconVolumeOn fas fa-volume-mute' : !propMuted && onoff ?
          'iconVolumeOff fas fa-volume-up' : !propMuted && onoff ? 'iconVolumeOn fas fa-volume-up' : 'iconVolumeOff fas fa-volume-mute'} />
      </div>
    </div>
  )
}

const mapStateToProps = (state) => {
  return { onoff: state.onoff }
}

export default connect(mapStateToProps)(VolumeSlider);
