import React from 'react';
import { connect } from 'react-redux';
import turnOnOff from '../../redux/actions/turnOnOff';
import store from '../../redux/store';




class PowerOnOff extends React.Component {

    constructor(props) {
        super(props);
        this.pessingButton = this.pressingButton.bind(this);
    }

    pessingButton() {
        this.props.turnOnOff(!store.getState());
    }

    render() {
        const { parOnOff, parChangeOnOff, onoff } = this.props;
        console.log(onoff);
        return <button className={`powerButton ${parOnOff ? 'powerOn' : 'powerOff'}`} onClick={this.pessingButton}><i className="fas fa-power-off" /></button>
    }

}

const mapStateToProps = (state) => {
    return {
        onoff: state.onoff,
    };
};

const mapDispatchToProps = {
    turnOnOff
};

export default connect(mapStateToProps, mapDispatchToProps)(PowerOnOff);
