import React from 'react';
import { connect } from 'react-redux';

const Display = (props) => {
    const { volStore, firstPlayList, playlist, secondPlayList, keyPressed, onOffStore } = props;
    return (
        <div className="display">
            <div>
                {playlist === 'CLASSIC' && keyPressed !== '' ?
                    firstPlayList.filter(x => x.key === keyPressed)[0].name.toUpperCase() :
                    playlist === 'ELECTRO' && keyPressed !== '' ?
                        secondPlayList.filter(x => x.key === keyPressed)[0].name.toUpperCase() :
                        ''
                }
            </div>
            <div>{onOffStore ? `VOL:${volStore}` : ''}</div>
        </div>
    )
}

function mapStateToProps(state) {
    return {
        onOffStore: state.onoff,
        volStore: state.volume
    };
}

export default connect(mapStateToProps)(Display);
