import React from 'react';
import { connect } from 'react-redux';
import store from '../../redux/store'
import changeVolume from '../../redux/actions/changeVolume'

class VolumeSlider extends React.Component {
  constructor(props) {
    super(props);
    this.changVol = this.changVol.bind(this);
    this.muteVolume = this.muteVolume.bind(this);
  }

  changVol(event) {
    this.props.changeVolume(event.target.value);
    console.log(store.getState());
  }

  muteVolume = () => {
    console.log(store.getState());

    this.props.changeVolume(0);
    console.log(store.getState());

  }

  render() {
    const { onoff, volumeProp } = this.props;

    return (
      <div className="divVolumeSlider" >
        <div className='inputSliderWrapper'>
          <input className={onoff ? 'sliderOn' : 'sliderOff'} type="range" min='0' max='100' value={volumeProp} onChange={this.changVol}></input>
        </div>
        <div className='iconWrapper'>
          <i onClick={this.muteVolume} className={volumeProp == 0 && onoff ? 'iconVolumeOn fas fa-volume-mute' : volumeProp != 0 && onoff ?
            'iconVolumeOff fas fa-volume-up' : volumeProp != 0 && onoff ? 'iconVolumeOn fas fa-volume-up' : 'iconVolumeOff fas fa-volume-mute'} />
        </div>
      </div>)
  }
}

const mapStateToProps = (state) => {
  return {
    onoff: state.onoff,
    volumeProp: state.volume
  }
}

const mapDispatchToProps = {
  changeVolume
}

export default connect(mapStateToProps, mapDispatchToProps)(VolumeSlider);
