import React from 'react';


class DrumVisualizer extends React.Component{
    
    
    shouldComponentUpdate(nextProps, nextState){
        console.log(nextProps);
        console.log(nextState);
        
        
        return nextProps.keyPressed === '' && nextProps.parVolume != 50? false: true;
    }
    
    

    render(){
        return(
            <div className="drumVisualizer">
                <div>
                {this.props.playlist === 'BANK 1' && this.props.keyPressed !== ''? 
                    this.props.firstPlayList.filter(x => x.key === this.props.keyPressed)[0].id :
                        this.props.playlist === 'BANK 2' && this.props.keyPressed !== ''? 
                            this.props.secondPlayList.filter(x => x.key === this.props.keyPressed)[0].id: 
                                ''  
                }

                </div>
                <div>Vol:{this.props.parVolume}</div>
                
            </div>
        )
    }
}


export default DrumVisualizer;
