import React from 'react';
import ReactDOM from 'react-dom';
import './App.css';
import {AwesomeButton} from "react-awesome-button";
import 'react-awesome-button/dist/themes/theme-c137.css';

const bankOne = [{
  keyCode: 81,
  keyTrigger: 'Q',
  id: 'Heater-1',
  url: 'https://s3.amazonaws.com/freecodecamp/drums/Heater-1.mp3'
}, {
  keyCode: 87,
  keyTrigger: 'W',
  id: 'Heater-2',
  url: 'https://s3.amazonaws.com/freecodecamp/drums/Heater-2.mp3'
}, {
  keyCode: 69,
  keyTrigger: 'E',
  id: 'Heater-3',
  url: 'https://s3.amazonaws.com/freecodecamp/drums/Heater-3.mp3'
}, {
  keyCode: 65,
  keyTrigger: 'A',
  id: 'Heater-4',
  url: 'https://s3.amazonaws.com/freecodecamp/drums/Heater-4_1.mp3'
}, {
  keyCode: 83,
  keyTrigger: 'S',
  id: 'Clap',
  url: 'https://s3.amazonaws.com/freecodecamp/drums/Heater-6.mp3'
}, {
  keyCode: 68,
  keyTrigger: 'D',
  id: 'Open-HH',
  url: 'https://s3.amazonaws.com/freecodecamp/drums/Dsc_Oh.mp3'
}, {
  keyCode: 90,
  keyTrigger: 'Z',
  id: "Kick-n'-Hat",
  url: 'https://s3.amazonaws.com/freecodecamp/drums/Kick_n_Hat.mp3'
}, {
  keyCode: 88,
  keyTrigger: 'X',
  id: 'Kick',
  url: 'https://s3.amazonaws.com/freecodecamp/drums/RP4_KICK_1.mp3'
}, {
  keyCode: 67,
  keyTrigger: 'C',
  id: 'Closed-HH',
  url: 'https://s3.amazonaws.com/freecodecamp/drums/Cev_H2.mp3'
},
];



class Pad extends React.Component{
  constructor(props){
    super(props);
    this.makeSound = this.makeSound.bind(this);
    this.handleKeyDown=this.handleKeyDown.bind(this);
    this.audio = React.createRef();
  }

  makeSound(e){
    if(this.props.paramOnOff === 'ON') this.audio.current.play();
  }

  handleKeyDown(e){
    if(e.key.toUpperCase() === this.props.paramLetter) this.audio.current.play();
  }

  componentWillMount(){
    document.addEventListener("keydown",this.handleKeyDown.bind(this));
  }

  render() {
    console.log("Rendering child");
    return(
      <div onClick={this.makeSound} className='drum-pad'>
        <audio src={this.props.paramSound} className="aud" ref={this.audio}></audio> 
        <button>{this.props.paramLetter}</button>
        {/*<AwesomeButton type="primary" >{this.props.paramLetter}</AwesomeButton>*/}
      </div>
    );
  }
}


class App extends React.Component{
  
  constructor(props){
    super(props);
    this.state = {
      sound : '',
      number: '',
      playList: 'MODERN',
      onOff: 'ON'
    };
    this.allSounds = '';
    this.importAll = this.importAll.bind(this);    
    this.changeOnOff = this.changeOnOff.bind(this);
    this.changePlayList = this.changePlayList.bind(this);
  }

  importAll (r) {
    let sounds = {};
    r.keys().map((item, index) => { sounds[item.replace('./', '')] = r(item); });
    return sounds;
  } 
  
  changeOnOff(){
    this.setState((state) => ({
      onOff: state.onOff === 'ON'? 'OFF':'ON'
    }));
  }

  changePlayList(){
    this.setState((state) => ({
      playList: state.playList === 'MODERN'? 'CLASSIC':'MODERN'
    }));
  }

  componentWillMount(){
    console.log('Component will mount');
    this.allSounds = this.importAll(require.context('./sounds', true, /\.(mp3)$/));  //second parameter: -true- to search in subfolders
  }
  
  render() {
    console.log('Rendering parent');
    return(
      <div className="App">      
        <div className="drumMachine">
          <div className="Grid" id="drum-machine">
              <Pad paramLetter='Q' paramOnOff={this.state.onOff} paramSound={this.state.playList === 'MODERN'? bankOne[0].url: Object.values(this.allSounds)[9]}></Pad>
              <Pad paramLetter='W' paramOnOff={this.state.onOff} paramSound={this.state.playList === 'MODERN'? bankOne[1].url: Object.values(this.allSounds)[10]}></Pad>
              <Pad paramLetter='E' paramOnOff={this.state.onOff} paramSound={this.state.playList === 'MODERN'? bankOne[2].url: Object.values(this.allSounds)[11]}></Pad>
              <Pad paramLetter='A' paramOnOff={this.state.onOff} paramSound={this.state.playList === 'MODERN'? bankOne[3].url: Object.values(this.allSounds)[12]}></Pad>
              <Pad paramLetter='S' paramOnOff={this.state.onOff} paramSound={this.state.playList === 'MODERN'? bankOne[4].url: Object.values(this.allSounds)[13]}></Pad>
              <Pad paramLetter='D' paramOnOff={this.state.onOff} paramSound={this.state.playList === 'MODERN'? bankOne[5].url: Object.values(this.allSounds)[14]}></Pad>
              <Pad paramLetter='Z' paramOnOff={this.state.onOff} paramSound={this.state.playList === 'MODERN'? bankOne[6].url: Object.values(this.allSounds)[15]}></Pad>
              <Pad paramLetter='X' paramOnOff={this.state.onOff} paramSound={this.state.playList === 'MODERN'? bankOne[7].url: Object.values(this.allSounds)[16]}></Pad>
              <Pad paramLetter='C' paramOnOff={this.state.onOff} paramSound={this.state.playList === 'MODERN'? bankOne[8].url: Object.values(this.allSounds)[17]}></Pad>
          </div>
          <div>
            <div onClick={this.changeOnOff}>
              {/*<AwesomeButton type="primary" className='onOffButton'>{this.state.onOff}</AwesomeButton>*/}
              <button>{this.state.onOff}</button>
            </div>
            <div onClick={this.changePlayList}>
              <button>{this.state.playList}</button>
              {/*<AwesomeButton type="primary" className='playlistButton'>{this.state.playList}</AwesomeButton>*/}
            </div>
          </div>
        </div>
      </div>
    );
  }
}



export default App;
