import React, {useState, useEffect} from 'react';



const Pad = (props) =>{
  useEffect(() =>{
    document.addEventListener("keydown", props.makeIt);
  });
  return(
    <div onClick={props.sendKey} className='drum-pad'>
      <audio src={props.paramSound} className="aud" ref={props.audioRef}></audio> 
      <button id='lmao' value={props.paramLetter}>{props.paramLetter}</button>
    </div>
  );
}


export default Pad;
