import React from 'react';




class DrumVisualizer extends React.Component{
    constructor(props){
        super(props);
        this.state = {key: props.keyPressed};
    }

    shouldComponentUpdate(nextProps, nextState){
        return nextProps.keyPressed === ''? false: true; 
    }

    render(){
        return(
            <button>
                { this.props.keyPressed
                    /*
                    props.playList === 'BANK 1'? 
                    props.firstPlayList.filter(x => x.key === props.keyPressed)[0].id : 
                    props.secondPlayList.filter(x => x.key === props.keyPressed)[0].id
                    */
                }
            </button>
        )
    }
}


export default DrumVisualizer;
