import React from 'react';
import store from '../../redux/store';
import {connect} from '../../redux/store';

const Display = (props) => {
    const { parVolume, firstPlayList, playlist, secondPlayList, keyPressed } = props;
    return (
        <div className="display">
            <div>
                {playlist === 'CLASSIC' && keyPressed !== '' ?
                    firstPlayList.filter(x => x.key === keyPressed)[0].name.toUpperCase() :
                    playlist === 'ELECTRO' && keyPressed !== '' ?
                        secondPlayList.filter(x => x.key === keyPressed)[0].name.toUpperCase() :
                        ''
                }
            </div>
            <div>{this.props.onoff ? `VOL:${parVolume}` : ''}</div>
        </div>
    )
}

function mapStateToProps (state) {
    return {
        onoff: state.onoff
    };
}



export default connect(mapStateToProps)(Display);
