import React from 'react';
import { connect } from 'react-redux';
import changePlaylist from '../../redux/actions/changePlayList';


class PlaylistSelector extends React.Component {
    constructor(props) {
        super(props);
        this.changePlayList = this.changePlayList.bind(this);
    }

    changePlayList(event) {
        this.props.changePlayList(event.currentTarget.innerHTML)
    }

    render() {
        const { onoff, playList, playListProp, changePlaylist } = this.props;
        return (
            < button className={`butPlayList ${onoff && playList === playListProp ? 'plListActive' : onoff && playList !== playListProp ? 'plListDisabled' : ''}`} onClick={changePlaylist} > {playList}</button >
        )
    }

}

function mapStateToProps(state) {
    return {
        onoff: state.onoff,
        playListProp: state.playList
    }
}

const mapDispatchToProps = state => {
    changePlaylist
}

export default connect(mapStateToProps, mapDispatchToProps)(PlaylistSelector);
