import React from 'react';
import { connect } from 'react-redux';



const PlaylistSelector = (props) => {
    const { onoff, playList, playListActive, changePlaylist } = props;
    return (
        < button className={`butPlayList ${onoff && playList === playListActive ? 'plListActive' : onoff && playList !== playListActive ? 'plListDisabled' : ''}`} onClick={changePlaylist} > {playList}</button >
    )
}


function mapStateToProps(state) {
    return {
        onoff: state.onoff
    }
}
export default connect(mapStateToProps)(PlaylistSelector);
