import React from 'react';
import './App.css';
import {AwesomeButton} from "react-awesome-button";
import 'react-awesome-button/dist/themes/theme-c137.css';



class Pad extends React.Component{
  constructor(props){
    super(props);
    this.state={
      letter: props.paramLetter,
      song: props.paramSound,
      onOff: props.paramOnOff
    };
    this.makeSound = this.makeSound.bind(this);
  }

  makeSound(e){
    console.log("Button pressed");
   e.currentTarget.getElementsByClassName("aud")[0].play();
  }

  render() {
    return(
      <div onClick={this.makeSound} className='drum-pad'>
        <audio src={this.state.song} className="aud"></audio> 
        <AwesomeButton type="primary" >{this.state.letter}</AwesomeButton>
      </div>
    );
  }
}


class App extends React.Component{
  
  constructor(props){
    super(props);
    this.state = {
      sound : '',
      number: '',
      playList: 'first',
      onOff: 'ON'
    };
    this.allSounds = '';
    this.importAll = this.importAll.bind(this);    
    this.changeOnOff = this.changeOnOff.bind(this);
    console.log('Constructing');
  }

  importAll (r) {
    let sounds = {};
    r.keys().map((item, index) => { sounds[item.replace('./', '')] = r(item); });
    return sounds;
  } 
  
  changeOnOff(){
    console.log("Changing onOFF");
    this.setState((state) => ({
      onOff: state.onOff === 'ON'? 'OFF':'ON'
    }));
    console.log('ESTADO ONOFF');
    console.log(this.state.onOff);
  }

  componentWillMount(){
    console.log('Component will mount');
    this.allSounds = this.importAll(require.context('./sounds', true, /\.(mp3)$/));  //second parameter: -true- to search in subfolders
    console.log(this.allSounds);
  }

  render() {
    console.log('Rendering');
    console.log(this.allSounds);
    return(
      <div className="App">      
        <div className="drumMachine">

          <div className="Grid" id="drum-machine">
              <Pad paramLetter='Q' paramSound={Object.values(this.allSounds)[0]} paramOnOff={this.state.onOFF}></Pad>
              <Pad paramLetter='W' paramSound={Object.values(this.allSounds)[1]}></Pad>
              <Pad paramLetter='E' paramSound={Object.values(this.allSounds)[2]}></Pad>
              <Pad paramLetter='A' paramSound={Object.values(this.allSounds)[3]}></Pad>
              <Pad paramLetter='S' paramSound={Object.values(this.allSounds)[4]}></Pad>
              <Pad paramLetter='D' paramSound={Object.values(this.allSounds)[5]}></Pad>
              <Pad paramLetter='Z' paramSound={Object.values(this.allSounds)[6]}></Pad>
              <Pad paramLetter='X' paramSound={Object.values(this.allSounds)[7]}></Pad>
              <Pad paramLetter='C' paramSound={Object.values(this.allSounds)[8]}></Pad>
          </div>
          <div onClick={this.changeOnOff}>
              <AwesomeButton type="primary" className='onOffButton' >{this.state.onOff}</AwesomeButton>
          </div>
        </div>
      </div>
    );
  }
}



export default App;
