import React from 'react';
import { connect } from 'react-redux';
import turnOnOff from '../../redux/actions/turnOnOff';
import store from '../../redux/store';



class PowerOnOff extends React.Component {

    constructor(props) {
        super(props);
        this.pressingButton = this.pressingButton.bind(this);
    }

    pressingButton() {
        this.props.turnOnOff(!store.getState().onoff);
    }

    render() {
        return <button className={`powerButton ${store.getState().onoff ? 'powerOn' : 'powerOff'}`} onClick={this.pressingButton}><i className="fas fa-power-off" /></button>
    }
}

const mapStateToProps = (state) => {
    return {
        onoff: state.onoff,
    };
};

const mapDispatchToProps = {
    turnOnOff
};

export default connect(mapStateToProps, mapDispatchToProps)(PowerOnOff);
