import React from 'react';
import store from '../../redux/store';

const Pad = (props) => {
  const { code, sendKey, keyActive, parLetter, parSound } = props;
  return (
    <button id={code} onClick={sendKey} className={keyActive === parLetter && store.getState().onoff ?
      'drum-pad drumOn drumActivated' : !store.getState().onoff ? 'drum-pad drumOff' : 'drum-pad drumOn'} value={parLetter}> {parLetter}
      <audio src={parSound} className="clip" id={parLetter}></audio>
    </button>
  );
}


export default Pad;
