import React from 'react';
import store from '../../redux/store';

const Display = (props) => {
    const { firstPlayList, playlist, secondPlayList, keyPressed } = props;
    return (
        <div className="display">
            <div>
                {playlist === 'CLASSIC' && keyPressed !== '' ?
                    firstPlayList.filter(x => x.key === keyPressed)[0].name.toUpperCase() :
                    playlist === 'ELECTRO' && keyPressed !== '' ?
                        secondPlayList.filter(x => x.key === keyPressed)[0].name.toUpperCase() :
                        ''
                }
            </div>
            <div>{store.getState().onoff ? `VOL:${parVolume}` : ''}</div>
        </div>
    )
}


export default Display;
