import React from 'react';
import { connect } from 'react-redux';
import changePlaylist from '../../redux/actions/changePlayList';


class PlaylistSelector extends React.Component {
    constructor(props) {
        super(props);
        this.changePlayList = this.changePlayList.bind(this);
    }

    changePlayList() {

    }

    render() {
        const { onoff, playList, playListActive, changePlaylist, playListProp } = props;
        return (
            < button className={`butPlayList ${onoff && playList === playListActive ? 'plListActive' : onoff && playList !== playListActive ? 'plListDisabled' : ''}`} onClick={changePlaylist} > {playList}</button >
        )
    }

}

function mapStateToProps(state) {
    return {
        onoff: state.onoff,
        playListProp: state.playList
    }
}

const mapDispatchToProps = state => {
    changePlaylist
}

export default connect(mapStateToProps, mapDispatchToProps)(PlaylistSelector);
