import React from 'react';
import store from '../../redux/store';

const VolumeSlider = (props) => {
  console.log("Rendering VolumeSlider");
    return(
      <div className="divVolumeSlider">
        <div className='inputSliderWrapper'>
          <input className={store.getState().onoff? 'sliderOn':'sliderOff'} type="range" min='0' max='100' value={props.vol} onChange={props.changeVol}></input>
        </div>
        <div className='iconWrapper'>
          <i onClick={props.muteVolume} className={props.propMuted && store.getState().onoff? 'iconVolumeOn fas fa-volume-mute': !props.propMuted && !store.getState().onoff?
          'iconVolumeOff fas fa-volume-up': !props.propMuted && store.getState().onoff? 'iconVolumeOn fas fa-volume-up': 'iconVolumeOff fas fa-volume-mute'}/>
        </div>
      </div>
    )
}

export default VolumeSlider;
