import React from 'react';
import Pad from './components/pad/Pad';
import VolumeSlider from './components/volumeSlider/VolumeSlider';
import PowerOnOff from './components/powerOnOff/PowerOnOff';
import Display from './components/display/Display';
import PlaylistSelector from './components/playlistSelector/PlaylistSelector';
import LogoHeader from './components/logoHeader/LogoHeader';
import './App.css';
import './components/volumeSlider/volumeSlider.css';
import './components/display/display.css';
import './components/pad/pad.css';
import './components/powerOnOff/powerOnOff.css';
import './components/playlistSelector/playlistSelector.css';
import './components/logoHeader/logoHeader.css';
import store from './redux/store';
import { playlistOne, playlistTwo } from './audios';
import { connect } from 'react-redux';


class App extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      playList: 'CLASSIC',
      keyPressed: '',
      volume: 50,
      muted: false,
      lastVolume: 0
    };
    this.aud = [];
    this.keyValidator = ['Q', 'W', 'E', 'A', 'S', 'D', 'Z', 'X', 'C'];
    this.firstPlayList = playlistOne;
    this.secondPlayList = playlistTwo;
  }

  componentWillMount() {
    document.addEventListener("keydown", this.makeSoundKeyDown.bind(this));
  }

  volumeHandler = (event) => {
    this.setState({ volume: event.target.value, muted: false });
  }

  makeSoundClick = (index, e) => {
    if (!this.props.onoff) return;
    var aud = e.currentTarget.querySelector('audio');
    aud.currentTime = 0;
    aud.volume = this.state.volume / 100;
    aud.play();
    this.setState({ keyPressed: e.currentTarget.value });
  }

  makeSoundKeyDown = (e) => {
    if (!this.props.onoff || this.keyValidator.indexOf(e.key.toUpperCase()) === -1) return;
    var aud = document.querySelector(`.clip[id=${e.key.toUpperCase()}]`);
    aud.currentTime = 0;
    aud.volume = this.state.volume / 100;
    aud.play();
    this.setState({ keyPressed: e.key.toUpperCase() });
  }


  changePlayList = (event) => {
    const playL = event.currentTarget.innerHTML;
    this.setState(() => ({
      playList: playL
    }));
  }

  muteVolume = () => {
    this.setState((state) =>
      state.muted === false ? { volume: 0, muted: true, lastVolume: state.volume } : { volume: state.lastVolume, muted: false }
    );
  }

  render() {
    return (
      <div className="App">
        <div className="drumMachine">
          <div className='divHeaderControls'>
            <div className='divHeader'>
              <PowerOnOff>{this.state.onOff}</PowerOnOff>
              <LogoHeader></LogoHeader>
            </div>
            <div className="Controls">
              <div className='divPlaySelectors'>
                <PlaylistSelector playList='CLASSIC' changePlaylist={this.changePlayList} playListActive={this.state.playList}></PlaylistSelector>
                <PlaylistSelector playList='ELECTRO' changePlaylist={this.changePlayList} playListActive={this.state.playList}></PlaylistSelector>
              </div>
              <Display playlist={this.state.playList} keyPressed={this.state.keyPressed} firstPlayList={this.firstPlayList} secondPlayList={this.secondPlayList} parVolume={this.state.volume}></Display>
              <VolumeSlider propMuted={this.state.muted} vol={this.state.volume} muteVolume={this.muteVolume} parVolume={this.state.volume} changeVol={this.volumeHandler}></VolumeSlider>
            </div>
          </div>
          <div className="Grid">
            {
              this.state.playList === 'CLASSIC' ?
                playlistOne.map((val, index) =>
                  <Pad code={val.code} parLetter={val.key} parVolume={this.state.volume} parSound={val.url} sendKey={this.makeSoundClick.bind(this, index)} key={index} keyActive={this.state.keyPressed}></Pad>
                ) :
                playlistTwo.map((val, index) =>
                  <Pad code={val.code} parLetter={val.key} parVolume={this.state.volume} parSound={val.url} sendKey={this.makeSoundClick.bind(this, index)} key={index} keyActive={this.state.keyPressed}></Pad>
                )
            }
          </div>
        </div>
      </div>
    );
  }
}


const mapStateToProps = (state) => {
  return { onoff: state.onoff }
}


export default connect(mapStateToProps)(App);
