import React from 'react';
import store from '../../redux/store';


const PlaylistSelector = (props) => <button className={`butPlayList ${store.getState().onoff && props.playList === props.playListActive ? 'plListActive' :
    store.getState().onoff && props.playList !== props.playListActive ? 'plListDisabled' : ''}`} onClick={props.changePlaylist}>{props.playList}</button>


function mapStateToProps(state) {
    return {
        onoff: state.onoff
    }
}
export default connect(mapStateToProps)(PlaylistSelector);
